import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { ENDPOINTS } from '../config/constants';

export default function Home() {
  const { user } = useUser();
  const navigate = useNavigate();
  const [ingredients, setIngredients] = useState('');
  const [calories, setCalories] = useState('');
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleGenerateRecipe = async () => {
    setLoading(true);
    setErrors({});
    try {
      const response = await api({
        url: ENDPOINTS.RECIPES.GENERATE,
        method: 'POST',
        body: {
          ingredients,
          calories: calories ? parseInt(calories) : null, // Optional calories
        },
      });
      // Navigate to the recipe page with the response
      navigate('/recipes', { state: { recipe: response.data.response } });
    } catch (err) {
      setErrors(err?.response?.data?.errors || {});
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container
      className="d-flex flex-column align-items-center justify-content-center"
      style={{
        minHeight: '100vh',
        backgroundImage: `url('/images/background.svg')`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
      }}
    >
      {/* Welcome Section */}
      <Row className="justify-content-center mb-4">
        <Col md={8} className="text-center">
          <h1 className="display-4">Welcome, {user?.name}!</h1>
          <p className="lead">Create your personalized recipe now.</p>
        </Col>
      </Row>

      {/* Form Section */}
      <Row className="justify-content-center">
        <Col md={6}>
          <Form>
            <Form.Group className="mb-3" controlId="ingredients">
              <Form.Label>Ingredients</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter ingredients (e.g., chicken, rice)"
                value={ingredients}
                onChange={(e) => setIngredients(e.target.value)}
                isInvalid={!!errors.ingredients}
              />
              <Form.Control.Feedback type="invalid">
                {errors.ingredients}
              </Form.Control.Feedback>
            </Form.Group>

            <Form.Group className="mb-3" controlId="calories">
              <Form.Label>Calories (optional)</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter calories"
                value={calories}
                onChange={(e) => setCalories(e.target.value)}
              />
            </Form.Group>

            <Button
              variant="primary"
              className="w-100"
              onClick={handleGenerateRecipe}
              disabled={loading}
            >
              {loading ? 'Generating Recipe...' : 'Generate Recipe'}
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}
