import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const { user } = useUser();
  const navigate = useNavigate();

  const [ingredients, setIngredients] = useState('');
  const [calories, setCalories] = useState('');

  const handleGenerateRecipe = () => {
    // Add logic here to send data to OpenAI API or other services
    console.log('Ingredients:', ingredients);
    console.log('Calories:', calories);
    navigate('/recipes'); // Redirect to Recipes page
  };

  return (
    <Container
      className="d-flex flex-column align-items-center justify-content-center"
      style={{
        minHeight: '100vh',
        backgroundImage: `url('/Image/background.svg')`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        color: 'white',
        textAlign: 'center',
        padding: '2rem',
      }}
    >

      {/* Welcome Section */}
      <div style={{ marginBottom: '50px' }}>
        <h1 className="display-4" style={{ color: 'white', fontWeight: 'bold' }}>
          Welcome, {user?.name}!
        </h1>
        <p style={{ color: 'white', fontSize: '18px', fontWeight: '500' }}>
          Generate your personalized recipe now.
        </p>
      </div>
        <Row className="justify-content-center">
          <Col md={8}>
            <Form>
              <Row className="mb-3">
                {/* Ingredients Field */}
                <Col md={10}>
                  <Form.Control
                    type="text"
                    placeholder="Enter ingredients (e.g., chicken, rice)"
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                    style={{
                      width: '130%', // Make it occupy the full width of its column
                      padding: '10px', // Add some padding for better UX
                      borderRadius: '5px',
                    }}
                  />
                </Col>

                {/* Calories Field */}
                <Col md={7}>
                  <Form.Control
                    type="text"
                    placeholder="Enter calories (optional)"
                    value={calories}
                    onChange={(e) => setCalories(e.target.value)}
                    style={{
                      width: '100%', // Make it occupy the full width of its column
                      padding: '10px', // Add some padding for better UX
                      borderRadius: '5px',
                    }}
                  />
                </Col>
              </Row>

              <Row className="text-center">
                {/* Generate Recipe Button */}
                <Button
                  onClick={handleGenerateRecipe}
                  style={{
                    backgroundColor: 'black',
                    border: 'none',
                    borderRadius: '5px',
                    padding: '10px 20px',
                  }}
                >
                  Generate Recipe
                </Button>
              </Row>
            </Form>
          </Col>
        </Row>
      </Container>
  );
}
