import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { ENDPOINTS } from '../config/constants';

export default function Home() {
  const { user } = useUser();
  const navigate = useNavigate();
  const [ingredients, setIngredients] = useState('');
  const [calories, setCalories] = useState('');
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleGenerateRecipe = async () => {
    setLoading(true);
    setErrors({});
    try {
      const response = await api({
        url: ENDPOINTS.RECIPES.GENERATE,
        method: 'POST',
        body: {
          ingredients,
          calories: calories ? parseInt(calories) : null, // Optional calories
        },
      });
      // Navigate to the recipe page with the response
      navigate('/recipes', { state: { recipe: response.data.response } });
    } catch (err) {
      setErrors(err?.response?.data?.errors || {});
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container
      className="d-flex flex-column align-items-center justify-content-center"
      style={{
        minHeight: '100vh',
        backgroundImage: `url('/Image/background.svg')`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        color: 'white',
        textAlign: 'center',
        padding: '2rem',
      }}
    >

      {/* Welcome Section */}
      <div style={{ marginBottom: '50px' }}>
        <h1 className="display-4" style={{ color: 'white', fontWeight: 'bold' }}>
          Welcome, {user?.name}!
        </h1>
        <p style={{ color: 'white', fontSize: '18px', fontWeight: '500' }}>
          Generate your personalized recipe now.
        </p>
      </div>

      {/* Form Section */}
      <Form>
        <Row className="justify-content-center align-items-center">
          {/* Ingredients Input */}
          <Col md={5} className="mb-3">
            <Form.Group controlId="ingredients">
              <Form.Control
                type="text"
                placeholder="Enter ingredients (e.g., chicken, rice)"
                value={ingredients}
                onChange={(e) => setIngredients(e.target.value)}
                isInvalid={!!errors.ingredients}
              />
              <Form.Control.Feedback type="invalid">
                {errors.ingredients}
              </Form.Control.Feedback>
            </Form.Group>
          </Col>

          {/* Calories Input */}
          <Col md={3} className="mb-3">
            <Form.Group controlId="calories">
              <Form.Control
                type="number"
                placeholder="Enter calories (optional)"
                value={calories}
                onChange={(e) => setCalories(e.target.value)}
              />
            </Form.Group>
          </Col>
        </Row>

        {/* Generate Recipe Button */}
        <Button
          variant="dark"
          className="mt-3"
          onClick={handleGenerateRecipe}
          disabled={loading}
          style={{
            fontWeight: 'bold',
            padding: '10px 50px',
          }}
        >
          {loading ? 'Generating...' : 'Generate Recipe'}
        </Button>
      </Form>
    </Container>
  );
}
