import React from 'react';
import { Container, Card } from 'react-bootstrap';
import { useLocation } from 'react-router-dom';

export default function Recipes() {
    const location = useLocation();
    const { recipe } = location.state || { recipe: 'No recipe available' };

    return (
        <Container
            className="d-flex flex-column align-items-center justify-content-center"
            style={{
                minHeight: '100vh',
                backgroundImage: `url('/Image/background.svg')`, // Background image
                backgroundSize: 'cover',
                backgroundRepeat: 'no-repeat',
                backgroundPosition: 'center',
                color: 'white',
                textAlign: 'center',
                padding: '2rem',
            }}
        >
            <Card
                style={{
                    backgroundColor: '#28a745', // Green background
                    color: 'white', // White text
                    padding: '20px',
                    borderRadius: '10px',
                    maxWidth: '600px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
                }}
            >
                <Card.Body>
                    <Card.Title style={{ fontSize: '1.8rem', fontWeight: 'bold' }}>
                        Generated Recipe
                    </Card.Title>
                    <Card.Text style={{ fontSize: '1.2rem', lineHeight: '1.8' }}>
                        {recipe}
                    </Card.Text>
                </Card.Body>
            </Card>
        </Container>
    );
}
