import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useUser } from '../../contexts/UserContext';
import { useNavigate } from 'react-router-dom';

export default function Profile() {
  const navigate = useNavigate();
  const { user } = useUser();

  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="shadow-sm mb-5">
            <Card.Body>
              <Card.Title>User Information</Card.Title>

              <Card.Text>
                <strong>Name:</strong> {user?.name} <br />
                <strong>Email:</strong> {user?.email} <br />
                <strong>Health Conditions: </strong> {user?.information?.health_conditions?.join(', ') || 'None'} <br />
                <strong>Dietary Preferences: </strong> {user?.information?.dietary_preferences?.join(', ') || 'None'} <br />
              </Card.Text>

              <Button variant="primary" className='me-2' onClick={() => navigate('/settings/information')}>
                Edit Information
              </Button>

              <Button variant="dark" className='me-2' onClick={() => navigate('/settings/information')}>
                Change Password
              </Button>

              {
                user?.has_TwoFA ? 
                <Button variant="warning" onClick={() => navigate('/settings/two-factor')}>
                  Disable Two-Factor Authentication
                </Button> :
                <Button variant="success" onClick={() => navigate('/settings/two-factor')}>
                  Enable Two-Factor Authentication
                </Button>
              }
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}
