import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { ENDPOINTS } from '../config/constants';

export default function Home() {
  const { user } = useUser();
  const navigate = useNavigate();
  const [ingredients, setIngredients] = useState('');
  const [calories, setCalories] = useState('');
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleGenerateRecipe = async () => {
    setLoading(true);
    setErrors({});
    try {
      const response = await api({
        url: ENDPOINTS.RECIPES.GENERATE,
        method: 'POST',
        body: {
          ingredients,
          calories: calories ? parseInt(calories) : null, // Optional calories
        },
      });
      // Navigate to the recipe page with the response
      navigate('/recipes', { state: { recipe: response.data.response } });
    } catch (err) {
      setErrors(err?.response?.data?.errors || {});
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container
      className="d-flex flex-column align-items-center justify-content-center"
      style={{
        minHeight: '100vh',
        backgroundImage: `url('/images/background.svg')`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
      }}
    >
      {/* Welcome Section */}
      <Row className="justify-content-center text-center mb-5">
        <Col md={8}>
          <h1 className="display-4 mb-3" style={{ color: 'white', fontWeight: 'bold' }}>
            Welcome, {user?.name}!
          </h1>
          <p className="lead" style={{ color: 'white' }}>
            Create your personalized recipe now.
          </p>
        </Col>
      </Row>

      {/* Form Section */}
      <Row className="justify-content-center">
        <Col md={8}>
          <Form>
            <Row className="align-items-center">
              {/* Ingredients Input */}
              <Col md={6} className="mb-3">
                <Form.Group controlId="ingredients">
                  <Form.Label style={{ color: 'white', fontWeight: 'bold' }}>Ingredients</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter ingredients (e.g., chicken, rice)"
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                    isInvalid={!!errors.ingredients}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.ingredients}
                  </Form.Control.Feedback>
                </Form.Group>
              </Col>

              {/* Calories Input */}
              <Col md={6} className="mb-3">
                <Form.Group controlId="calories">
                  <Form.Label style={{ color: 'white', fontWeight: 'bold' }}>Calories (optional)</Form.Label>
                  <Form.Control
                    type="number"
                    placeholder="Enter calories"
                    value={calories}
                    onChange={(e) => setCalories(e.target.value)}
                  />
                </Form.Group>
              </Col>
            </Row>

            {/* Generate Recipe Button */}
            <div className="text-center">
              <Button
                variant="dark"
                className="px-5"
                onClick={handleGenerateRecipe}
                disabled={loading}
                style={{ fontWeight: 'bold' }}
              >
                {loading ? 'Generating...' : 'Generate Recipe'}
              </Button>
            </div>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}
