import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Card } from 'react-bootstrap';
import { useUser } from '../../contexts/UserContext';
import api from '../../services/api';
import { ENDPOINTS } from '../../config/constants';

export default function Profile() {
  const { user, login } = useUser();
  const [name, setName] = useState(user?.name || '');
  const [email, setEmail] = useState(user?.email || '');
  const [password, setPassword] = useState('');
  const [healthConditions, setHealthConditions] = useState(user?.health_conditions || []);
  const [dietaryPreferences, setDietaryPreferences] = useState(user?.dietary_preferences || []);
  const [availableHealth, setAvailableHealth] = useState([]);
  const [availableDietary, setAvailableDietary] = useState([]);
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    // Fetch available health conditions and dietary preferences
    api({
      url: ENDPOINTS.USER.PROFILE + '/data',
      method: 'GET',
      onSuccess: (data) => {
        setAvailableHealth(data.health);
        setAvailableDietary(data.dietary);
      },
    });
  }, []);

  const handleSave = () => {
    setLoading(true);

    const updatedData = {
      name,
      email,
      password: password || undefined, // Only send if not empty
      health_conditions: healthConditions,
      dietary_preferences: dietaryPreferences,
    };

    api({
      url: ENDPOINTS.USER.PROFILE,
      method: 'PUT',
      body: updatedData,
      onSuccess: (data) => {
        setSuccessMessage('Profile updated successfully!');
        login(data.user, localStorage.getItem('token')); // Update context
      },
      onEnd: () => {
        setLoading(false);
      },
    });
  };

  return (
    <Container
      className="d-flex flex-column align-items-center justify-content-center"
      style={{
        minHeight: '100vh',
        backgroundImage: `url('/Image/background.svg')`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        padding: '2rem',
      }}
    >
      <Card className="p-4 shadow-lg" style={{ maxWidth: '600px', width: '100%' }}>
        <Card.Body>
          <h3 className="text-center mb-4">User Information</h3>
          {successMessage && <p style={{ color: 'green', textAlign: 'center' }}>{successMessage}</p>}
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter new password (leave blank to keep current)"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Health Conditions</Form.Label>
              <Form.Control
                as="select"
                multiple
                value={healthConditions}
                onChange={(e) => setHealthConditions([...e.target.selectedOptions].map((o) => o.value))}
              >
                {availableHealth.map((condition) => (
                  <option key={condition} value={condition}>
                    {condition}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Dietary Preferences</Form.Label>
              <Form.Control
                as="select"
                multiple
                value={dietaryPreferences}
                onChange={(e) => setDietaryPreferences([...e.target.selectedOptions].map((o) => o.value))}
              >
                {availableDietary.map((preference) => (
                  <option key={preference} value={preference}>
                    {preference}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <div className="d-flex justify-content-center">
              <Button
                onClick={handleSave}
                disabled={loading}
                style={{ backgroundColor: 'black', border: 'none', marginRight: '10px' }}
              >
                {loading ? 'Saving...' : 'Save Changes'}
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
}
